export interface ThemeDef {
  id: string;
  label: string;
  colors: {
    bg: string;       // app/panel outer bg
    panel: string;    // inner card bg (can be semi-transparent)
    header: string;   // header bg (gradient ok)
    text: string;     // text color
    accent: string;   // primary accent
    border: string;   // border color
    input: string;    // input bg
  };
  backgroundImage?: string; // e.g. url('/themes/xxx.jpg')
}

