export const themeMap: Record<string, ThemeDef> = Object.fromEntries(
  themeDefs.map(t => [t.id, t])
);

export function getTheme(id?: string): ThemeDef {
  if (!id) return themeMap["minimal"] || themeDefs[0];
  return themeMap[id] || themeMap["minimal"] || themeDefs[0];
}

// For selects: value/label pairs
export const themes = themeDefs.map(t => ({ value: t.id, label: t.label }));
